package IntervQS;

public class Substring2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	        String s = "abcd";
	        String sub = "cdr";

	        // Check if 'sub' is a substring of 's'
	        if (s.contains(sub)) {
	            System.out.println(sub + " is a substring of " + s);
	        } else {
	            System.out.println(sub + " is not a substring of " + s);
	        }
	    
	}

}
